pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/utils/Counters.sol';

/// @title Justine the Smart Co NFT Contract
/// @author Your Name
/// @notice This contract implements a standard ERC721 NFT with access control and security measures.
contract JustineTheSmartCo is ERC721, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    string private _baseTokenURI;

    event BaseTokenURIChanged(string newBaseTokenURI);

    /// @param _owner The address of the contract owner
    /// @param baseTokenURI The initial base URI for the NFT metadata
    constructor(address _owner, string memory baseTokenURI) 
        ERC721("Justine the Smart Co", "JSC")
        Ownable(_owner) 
        ReentrancyGuard() 
    {
        _baseTokenURI = baseTokenURI;
    }

    /// @notice Mint a new NFT to the specified address
    /// @param to The address to mint the NFT to
    /// @return The token ID of the minted NFT
    function mintNFT(address to) external onlyOwner nonReentrant returns (uint256) {
        require(to != address(0), "Cannot mint to the zero address");

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        _safeMint(to, tokenId);
        return tokenId;
    }

    /// @notice Set the base URI for the NFT metadata
    /// @param newBaseTokenURI The new base URI string
    function setBaseTokenURI(string memory newBaseTokenURI) external onlyOwner {
        _baseTokenURI = newBaseTokenURI;
        emit BaseTokenURIChanged(newBaseTokenURI);
    }

    /// @notice Retrieve the base URI for the NFT metadata
    /// @return The base URI string
    function _baseURI() internal view override returns (string memory) {
        return _baseTokenURI;
    }

    /// @notice Retrieve the total count of minted tokens
    /// @return The total supply of minted NFTs
    function totalSupply() external view returns (uint256) {
        return _tokenIdCounter.current();
    }
}
