pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/security/Pausable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';

/// @title Pausable ERC20 Token
/// @notice This token implements an ERC20 standard with pausable functionality.
contract PausableERC20Token is ERC20, Ownable, Pausable, ReentrancyGuard {
    uint8 private _decimals;

    event TokensBurned(address indexed account, uint256 amount);

    /// @dev Sets the values for {name}, {symbol}, and {decimals}. 
    /// The default value of {decimals} is set to 18.
    constructor(string memory name, string memory symbol, uint8 decimals, address _owner) 
        ERC20(name, symbol) 
        Ownable(_owner) {
        _decimals = decimals;
    }

    /// @dev Returns the number of decimals used to get its user representation.
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /// @dev See {ERC20-_beforeTokenTransfer}.
    /// Can be overridden to add custom logic during transfer.
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
    }

    /// @notice Pause token transfers. Only callable by the owner.
    function pause() external onlyOwner {
        _pause();
    }

    /// @notice Unpause token transfers. Only callable by the owner.
    function unpause() external onlyOwner {
        _unpause();
    }

    /// @notice Burns a specific amount of tokens from the caller's account.
    /// @param amount The amount of token to be burned.
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
        emit TokensBurned(msg.sender, amount);
    }

    /// @dev Overrides {ERC20-transfer} to respect the paused state.
    function transfer(address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(recipient, amount);
    }

    /// @dev Overrides {ERC20-transferFrom} to respect the paused state.
    function transferFrom(address sender, address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    /// @dev Overrides {ERC20-approve} to add requirements and also to respect paused state (if needed).
    function approve(address spender, uint256 amount) public override whenNotPaused returns (bool) {
        return super.approve(spender, amount);
    }
}
