pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20Token
 * @dev Implementation of a pausable ERC20 token with access control and reentrancy guards.
 */
contract PausableERC20Token is ERC20, Ownable, ReentrancyGuard {
    bool private _paused;

    event Paused();
    event Unpaused();

    /**
     * @dev Sets the values for {name} and {symbol}.
     * Initializes the contract in unpaused state.
     */
    constructor(string memory name, string memory symbol, address _owner) 
        ERC20(name, symbol) 
        Ownable(_owner) 
    {
        _paused = false;
    }

    /**
     * @dev Returns true if the contract is paused, false otherwise.
     */
    function paused() public view returns (bool) {
        return _paused;
    }

    /**
     * @dev Pauses the contract, preventing any transfers.
     * Can only be called by the owner.
     */
    function pause() external onlyOwner {
        _paused = true;
        emit Paused();
    }

    /**
     * @dev Unpauses the contract, allowing transfers.
     * Can only be called by the owner.
     */
    function unpause() external onlyOwner {
        _paused = false;
        emit Unpaused();
    }

    /**
     * @dev Override the transfer function to prevent transfers when paused.
     */
    function transfer(address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev Override the transferFrom function to prevent transfers when paused.
     */
    function transferFrom(address sender, address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transferFrom(sender, recipient, amount);
    }

    /**
     * @dev Prevent transfers when paused.
     */
    modifier whenNotPaused() {
        require(!_paused, "Pausable: token transfer while paused");
        _;
    }
}
