pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title RAG Conte Token
 * @dev ERC20 Token with Ownable and Reentrancy protections
 */
contract RAGConteToken is ERC20, Ownable, ReentrancyGuard {

    uint256 private _cap;

    event CapChanged(uint256 oldCap, uint256 newCap);

    /**
     * @dev Sets the values for {name} and {symbol}, initializes the cap, and assigns the initial supply to the owner.
     * @param _owner The address of the contract owner
     * @param initialSupply The initial supply of tokens
     * @param cap The maximum supply of tokens
     */
    constructor(address _owner, uint256 initialSupply, uint256 cap) 
        ERC20("RAG Conte", "RAGC") 
        Ownable(_owner) 
    {
        require(cap > 0, "Cap must be greater than 0");
        _cap = cap;
        _mint(_owner, initialSupply);
    }

    /**
     * @dev Mints new tokens to a specified address.
     * @param account The address to mint tokens to
     * @param amount The amount of tokens to mint
     */
    function mint(address account, uint256 amount) public onlyOwner nonReentrant {
        require(totalSupply() + amount <= _cap, "Cap exceeded");
        _mint(account, amount);
    }

    /**
     * @dev Returns the cap on the token's total supply.
     */
    function cap() public view returns (uint256) {
        return _cap;
    }

    /**
     * @dev Sets a new cap for the token's total supply.
     * @param newCap The new cap to set
     */
    function setCap(uint256 newCap) external onlyOwner {
        require(newCap >= totalSupply(), "New cap must be greater than total supply");
        emit CapChanged(_cap, newCap);
        _cap = newCap;
    }

    /**
     * @dev Overrides the _beforeTokenTransfer hook to include any custom logic before a token transfer.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        super._beforeTokenTransfer(from, to, amount);
        // Custom logic can be added here if required
    }
}

