pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20Token
 * @dev Implementation of a pausable ERC20 token with owner controls.
 */
contract PausableERC20Token is ERC20, Ownable, ReentrancyGuard {
    bool private _paused;

    event Paused();
    event Unpaused();

    modifier whenNotPaused() {
        require(!_paused, "Contract is paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Contract is not paused");
        _;
    }

    constructor(string memory name, string memory symbol, address initialOwner) 
        ERC20(name, symbol) 
        Ownable(initialOwner) 
    {
        _paused = false;
    }

    /**
     * @dev Pause the contract, disabling token transfers.
     */
    function pause() external onlyOwner whenNotPaused {
        _paused = true;
        emit Paused();
    }

    /**
     * @dev Unpause the contract, enabling token transfers.
     */
    function unpause() external onlyOwner whenPaused {
        _paused = false;
        emit Unpaused();
    }

    /**
     * @dev See {ERC20-transfer}.
     */
    function transfer(address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev See {ERC20-transferFrom}.
     */
    function transferFrom(address sender, address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transferFrom(sender, recipient, amount);
    }

    /**
     * @dev See {ERC20-approve}.
     */
    function approve(address spender, uint256 amount) 
        public 
        override 
        returns (bool) 
    {
        return super.approve(spender, amount);
    }

    /**
     * @dev See {ERC20-increaseAllowance}.
     */
    function increaseAllowance(address spender, uint256 addedValue) 
        public 
        returns (bool) 
    {
        return super.increaseAllowance(spender, addedValue);
    }

    /**
     * @dev See {ERC20-decreaseAllowance}.
     */
    function decreaseAllowance(address spender, uint256 subtractedValue) 
        public 
        returns (bool) 
    {
        return super.decreaseAllowance(spender, subtractedValue);
    }
}

