pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title CreateERC20Token
 * @dev A simple ERC20 token contract with ownership and reentrancy protection.
 */
contract CreateERC20Token is ERC20, Ownable, ReentrancyGuard {
    uint256 private constant MAX_SUPPLY = 1000000 * 10 ** 18; // Total supply of 1 million tokens with 18 decimal places

    /**
     * @dev Emitted when tokens are minted.
     * @param to The address to which tokens are minted.
     * @param amount The amount of tokens minted.
     */
    event TokensMinted(address indexed to, uint256 amount);

    /**
     * @param _owner Address of the owner of the contract.
     */
    constructor(address _owner) ERC20("MyToken", "MTK") Ownable(_owner) {
        _mint(_owner, MAX_SUPPLY); // Mint the total supply to the owner
    }

    /**
     * @dev Mint new tokens to a specified address (only owner).
     * @param to Address to which the tokens will be minted.
     * @param amount Amount of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner nonReentrant {
        require(to != address(0), "Invalid address");
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }

    /**
     * @dev Override the _beforeTokenTransfer hook from ERC20.
     * @param from Address sending the tokens.
     * @param to Address receiving the tokens.
     * @param amount Amount of tokens to transfer.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);
    }
}
