pragma solidity ^0.8.0;

import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/utils/Counters.sol';

/**
 * @title JustineTheSmartCo
 * @dev A simple ERC721 contract for creating NFTs with ownership and security features.
 */
contract JustineTheSmartCo is ERC721, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    event Minted(uint256 indexed tokenId, address indexed owner);

    /**
     * @dev Constructor for the JustineTheSmartCo contract.
     * @param _owner Address of the contract owner.
     */
    constructor(address _owner) ERC721("JustineTheSmartCo", "JSC") Ownable(_owner) {}

    /**
     * @dev Mint a new NFT to the caller's address.
     * @return tokenId The ID of the minted token.
     */
    function mint() external nonReentrant returns (uint256 tokenId) {
        tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _mint(msg.sender, tokenId);
        emit Minted(tokenId, msg.sender);
        return tokenId;
    }

    /**
     * @dev See {ERC721-_baseURI}.
     * @return baseURI The base URI for token metadata.
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return "https://api.example.com/metadata/"; // Replace with actual metadata URI
    }
}

