pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/utils/Counters.sol';

/**
 * @title JustineNFT
 * @dev This contract creates an ERC721 NFT collection named "Justine" with a total supply of 1,000.
 */
contract JustineNFT is ERC721, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIdCounter;

    uint256 public constant MAX_SUPPLY = 1000;

    event Minted(address indexed to, uint256 indexed tokenId);

    constructor(address _owner) ERC721("Justine", "JST") Ownable(_owner) {
        // Initialization code if needed
    }

    /**
     * @dev Mint a new NFT to the specified address.
     * Requirements:
     * - The total supply must be less than MAX_SUPPLY.
     */
    function mint(address to) external onlyOwner nonReentrant {
        require(_tokenIdCounter.current() < MAX_SUPPLY, "Max supply reached");

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        _safeMint(to, tokenId);
        emit Minted(to, tokenId);
    }

    /**
     * @dev Returns the current token ID counter.
     * @return The current token ID counter.
     */
    function getCurrentTokenId() external view returns (uint256) {
        return _tokenIdCounter.current();
    }
}

