pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20
 * @dev ERC20 Token that can be paused and is governed by an owner.
 */
contract PausableERC20 is ERC20, Ownable, ReentrancyGuard {
    bool private _paused;

    event Paused();
    event Unpaused();

    modifier whenNotPaused() {
        require(!_paused, "Token is paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Token is not paused");
        _;
    }

    constructor(string memory name, string memory symbol, address _owner) 
        ERC20(name, symbol) 
        Ownable(_owner) 
    {
        _paused = false;
    }

    /**
     * @dev Pause the token transfers.
     * Only callable by the owner.
     */
    function pause() external onlyOwner whenNotPaused {
        _paused = true;
        emit Paused();
    }

    /**
     * @dev Unpause the token transfers.
     * Only callable by the owner.
     */
    function unpause() external onlyOwner whenPaused {
        _paused = false;
        emit Unpaused();
    }

    /**
     * @dev Transfers `amount` tokens from the caller to `recipient`.
     * This function is overridden to include the pause check.
     */
    function transfer(address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev Transfers `amount` tokens from `sender` to `recipient`.
     * This function is overridden to include the pause check.
     */
    function transferFrom(address sender, address recipient, uint256 amount) 
        public 
        override 
        whenNotPaused 
        returns (bool) 
    {
        return super.transferFrom(sender, recipient, amount);
    }
}

