pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20Token
 * @dev This contract is a standard ERC20 token with pausable functionality.
 * It includes access controls, reentrancy guards, and error handling to ensure security.
 */
contract PausableERC20Token is ERC20Pausable, Ownable, ReentrancyGuard {
    uint256 private _maxSupply;

    event Mint(address indexed to, uint256 amount);
    event Burn(address indexed from, uint256 amount);

    /**
     * @dev Sets the name and symbol of the token and initializes the max supply.
     * @param _owner The address of the contract owner.
     * @param name The name of the token.
     * @param symbol The symbol of the token.
     * @param maxSupply The maximum supply of the token.
     */
    constructor(address _owner, string memory name, string memory symbol, uint256 maxSupply) 
        ERC20(name, symbol) 
        Ownable(_owner) 
    {
        _maxSupply = maxSupply;
    }

    /**
     * @dev Allows the owner to mint new tokens, only if it does not exceed the max supply.
     * @param to The address receiving the minted tokens.
     * @param amount The amount of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner nonReentrant {
        require(totalSupply() + amount <= _maxSupply, "Supply cap exceeded");
        _mint(to, amount);
        emit Mint(to, amount);
    }

    /**
     * @dev Allows the owner to burn tokens from a specific address.
     * @param from The address owning the tokens.
     * @param amount The amount of tokens to burn.
     */
    function burn(address from, uint256 amount) external onlyOwner nonReentrant {
        _burn(from, amount);
        emit Burn(from, amount);
    }

    /**
     * @dev Pauses the token transfers. Only callable by the owner.
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpauses the token transfers. Only callable by the owner.
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Overrides the _beforeTokenTransfer hook from ERC20Pausable to check for the paused state.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @return The maximum supply of the token.
     */
    function maxSupply() external view returns (uint256) {
        return _maxSupply;
    }
}

