pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/extensions/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title SimpleERC20Token
 * @dev A simple ERC20 token contract with ownership and reentrancy protection.
 */
contract SimpleERC20Token is ERC20, Ownable, ReentrancyGuard {
    uint8 private _decimals;

    /**
     * @dev Emitted when the token is minted.
     * @param to The address which received the minted tokens.
     * @param amount The amount of tokens minted.
     */
    event TokensMinted(address indexed to, uint256 amount);

    /**
     * @dev Sets the values for {name}, {symbol}, and {decimals}.
     * The default value of {decimals} is 18.
     *
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor(string memory name, string memory symbol, uint8 decimals_) 
        ERC20(name, symbol) 
        Ownable(msg.sender) 
    {
        _decimals = decimals_;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     */
    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    /**
     * @dev Mints new tokens to the specified address.
     * @param to The address to mint tokens to.
     * @param amount The amount of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner nonReentrant {
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }

    /**
     * @dev Internal function to transfer tokens between addresses.
     * @param sender The address from which to transfer.
     * @param recipient The address to which to transfer.
     * @param amount The amount of tokens to transfer.
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "ERC20: transfer amount must be greater than zero");

        super._transfer(sender, recipient, amount);
    }
}
