pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title CreateERC20Token
 * @dev Implementation of the ERC20 Token with Ownable access control and ReentrancyGuard security.
 */
contract CreateERC20Token is ERC20, Ownable, ReentrancyGuard {
    uint256 private _maxSupply;

    event MaxSupplyUpdated(uint256 newMaxSupply);

    /**
     * @dev Sets the values for {name} and {symbol}, and initializes the max supply.
     * @param _owner The address of the owner of the token.
     * @param name_ The name of the token.
     * @param symbol_ The symbol of the token.
     * @param maxSupply_ The maximum supply of the token.
     */
    constructor(address _owner, string memory name_, string memory symbol_, uint256 maxSupply_) 
        ERC20(name_, symbol_) 
        Ownable(_owner) 
    {
        _maxSupply = maxSupply_;
    }

    /**
     * @dev Mint tokens to a specified address.
     * Requirements:
     * - The caller must be the owner.
     * - The total supply must not exceed the maximum supply.
     * @param to Address to receive the minted tokens.
     * @param amount The amount of tokens to mint.
     */
    function mint(address to, uint256 amount) external onlyOwner nonReentrant {
        require(totalSupply() + amount <= _maxSupply, "Exceeded max supply");
        _mint(to, amount);
    }

    /**
     * @dev Set the maximum supply of the token.
     * Requirements:
     * - Only the owner can call this function.
     * @param newMaxSupply The new maximum supply.
     */
    function setMaxSupply(uint256 newMaxSupply) external onlyOwner {
        require(newMaxSupply >= totalSupply(), "New max supply must be >= total supply");
        _maxSupply = newMaxSupply;
        emit MaxSupplyUpdated(newMaxSupply);
    }

    /**
     * @dev Get the current maximum supply of the token.
     * @return The maximum supply of the token.
     */
    function maxSupply() external view returns (uint256) {
        return _maxSupply;
    }
}
