pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/utils/Counters.sol';

/**
 * @title JustineNFT
 * @dev Implementation of ERC721 Non-Fungible Token with access control and security features.
 */
contract JustineNFT is ERC721, Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIdCounter;

    // Event emitted when a new NFT is created
    event NFTCreated(uint256 indexed tokenId, address indexed owner);

    /**
     * @dev Constructor function
     * @param _owner Address of the contract owner
     */
    constructor(address _owner) ERC721("Justine", "JSTN") Ownable(_owner) {}

    /**
     * @dev Function to mint a new NFT
     * @param to The address that will receive the minted NFT
     * @return tokenId The ID of the minted token
     */
    function mintNFT(address to) external onlyOwner nonReentrant returns (uint256 tokenId) {
        require(to != address(0), "Invalid address");

        tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        _mint(to, tokenId);

        emit NFTCreated(tokenId, to);
    }

    /**
     * @dev Function to get the current token ID counter
     * @return currentCount The current count of minted tokens
     */
    function currentTokenId() external view returns (uint256 currentCount) {
        return _tokenIdCounter.current();
    }
}
