pragma solidity ^0.8.0;

import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20Token
 * @dev ERC20 Token that can be paused by the owner.
 */
contract PausableERC20Token is ERC20, Ownable, ReentrancyGuard {
    bool private _paused;

    event Paused();
    event Unpaused();

    modifier whenNotPaused() {
        require(!_paused, "Pausable: paused");
        _;
    }

    modifier whenPaused() {
        require(_paused, "Pausable: not paused");
        _;
    }

    /**
     * @dev Sets the values for {name} and {symbol}, and initializes
     * the contract in an unpaused state.
     */
    constructor(string memory name_, string memory symbol_, address _owner)
        ERC20(name_, symbol_)
        Ownable(_owner) 
    {
        _paused = false;
    }

    /**
     * @dev Pause the contract, preventing all token transfers.
     * Can only be called by the current owner.
     */
    function pause() external onlyOwner whenNotPaused {
        _paused = true;
        emit Paused();
    }

    /**
     * @dev Unpause the contract, allowing all token transfers.
     * Can only be called by the current owner.
     */
    function unpause() external onlyOwner whenPaused {
        _paused = false;
        emit Unpaused();
    }

    /**
     * @dev Transfers tokens. Can only be executed when the contract is not paused.
     * Overrides the parent function to enforce pause behavior.
     */
    function transfer(address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(recipient, amount);
    }

    /**
     * @dev Transfers tokens from one account to another. Can only be executed when the contract is not paused.
     * Overrides the parent function to enforce pause behavior.
     */
    function transferFrom(address sender, address recipient, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transferFrom(sender, recipient, amount);
    }

    /**
     * @dev Mints tokens to an account. Can only be called by the current owner.
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Burns tokens from an account. Can only be called by the current owner.
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
    }

    /**
     * @dev Checks if the contract is paused.
     */
    function paused() external view returns (bool) {
        return _paused;
    }
}
