pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC20/extensions/ERC20Pausable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title PausableERC20Token
 * @dev ERC20 Token that can be paused by the owner.
 */
contract PausableERC20Token is ERC20Pausable, Ownable, ReentrancyGuard {
    uint256 private immutable _initialSupply;

    /**
     * @dev Sets the values for {name}, {symbol} and {initialSupply}.
     */
    constructor(string memory name, string memory symbol, uint256 initialSupply) 
        ERC20(name, symbol) 
        Ownable(msg.sender) 
        ReentrancyGuard() 
    {
        require(initialSupply > 0, "Initial supply must be greater than 0");
        _initialSupply = initialSupply;
        _mint(msg.sender, initialSupply);
    }

    /**
     * @dev Pause the contract.
     * Only callable by the owner.
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract.
     * Only callable by the owner.
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override `_beforeTokenTransfer` to implement pausable functionality.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override(ERC20, ERC20Pausable) {
        super._beforeTokenTransfer(from, to, amount);
    }

    /**
     * @dev Returns the initial supply of the token.
     */
    function initialSupply() external view returns (uint256) {
        return _initialSupply;
    }
}
