pragma solidity ^0.8.0;

import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC20/ERC20.sol';
import '@openzeppelin/contracts/utils/Address.sol';

/**
 * @title RAG Conte ERC20 Token
 * @dev This contract implements a standard ERC20 token with ownership and reentrancy protection.
 */
contract RAGConteToken is ERC20, Ownable, ReentrancyGuard {
    // Additional event specific to this token
    event TokenMinted(address indexed to, uint256 amount);

    /**
     * @dev Sets the values for {name} and {symbol} of the token.
     * The default value of {decimals} is 18.
     * Initial token supply is assigned to the owner.
     */
    constructor(address _owner, string memory name, string memory symbol) 
        ERC20(name, symbol) 
        Ownable(_owner) 
    {
        // Mint some initial tokens to the owner
        _mint(_owner, 1000000 * 10 ** decimals()); // Adjust initial supply as needed
    }

    /**
     * @dev Mint new tokens to a specified address.
     * Can only be called by the owner.
     * Emits a {TokenMinted} event.
     */
    function mint(address to, uint256 amount) external onlyOwner nonReentrant {
        _mint(to, amount);
        emit TokenMinted(to, amount);
    }

    /**
     * @dev Internal function to invoke the token transfer hook.
     */
    function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
        super._beforeTokenTransfer(from, to, amount);
        // Custom logic can be placed here if needed in the future
    }
}

